import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Create() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [age, setAge] = useState(null);
  const [error, setError] = useState('');

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const addUser = { name, email, age };

    const response = await fetch('http://localhost:5000', {
      method: 'POST',
      body: JSON.stringify(addUser),
      headers: {
        'Content-Type': 'application/json',
      },
    });

    const result = await response.json();

    if (!response.ok) {
      console.log(result.error);
      setError(result.error);
    }

    if (response.ok) {
      console.log(result);
      setError('');
      setName('');
      setEmail('');
      setAge(0);
      navigate('/all');
    }
  };

return (
  <div className="min-h-screen bg-black text-white">
    <div className="container mx-auto flex-1 px-4 py-8 items-center justify-center">
      {/* ... */}
      <form onSubmit={handleSubmit} className="grid grid-cols-1 gap-4 md:grid-cols-2">
        {/* ... */}
        <div className="mb-4 md:col-span-2">
          <label className="block text-gray-300 text-sm font-semibold mb-2">Email Address</label>
          <input
            type="email"
            className="w-full px-4 py-2 border border-gray-400 rounded-md focus:outline-none focus:border-blue-500 bg-gray-500 text-gray-200 text-sm"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="mb-4 md:col-span-2">
          <label className="block text-gray-300 text-sm font-semibold mb-2">Age</label>
          <input
            type="number"
            className="w-full px-4 py-2 border border-gray-400 rounded-md focus:outline-none focus:border-blue-500 bg-gray-500 text-gray-200 text-sm"
            value={age}
            onChange={(e) => setAge(e.target.value)}
          />
        </div>
        <div className="mb-4 md:col-span-2 text-center">
          <button
            type="submit"
            className="w-full bg-red-600 text-white p-2 rounded-md hover:bg-red-700 text-sm"
          >
            Submit Ghost Story
          </button>
        </div>
      </form>
    </div>
  </div>
);
}

export default Create;
