import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Create() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [age, setAge] = useState(null);
  const [error, setError] = useState('');

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const addUser = { name, email, age };

    const response = await fetch('http://localhost:5000', {
      method: 'POST',
      body: JSON.stringify(addUser),
      headers: {
        'Content-Type': 'application/json',
      },
    });

    const result = await response.json();

    if (!response.ok) {
      console.log(result.error);
      setError(result.error);
    }

    if (response.ok) {
      console.log(result);
      setError('');
      setName('');
      setEmail('');
      setAge(0);
      navigate('/all');
    }
  };

  return (
    <div className="container mx-auto my-12 max-w-md p-8 bg-black text-white shadow-lg rounded-lg">
      {error && <div className="text-red-500 mb-4">{error}</div>}
      <h2 className="text-4xl font-extrabold text-center mb-6">Share Your Ghost Story</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-8">
          <label className="block text-gray-400 text-sm font-semibold mb-2">Ghost Story</label>
          <textarea
            className="w-full px-4 py-2 border border-gray-600 rounded-md focus:outline-none focus:border-blue-500 bg-gray-700 text-gray-200"
            value={name}
            onChange={(e) => setName(e.target.value)}
            rows="6"
          />
        </div>
        <div className="mb-6">
          <label className="block text-gray-400 text-sm font-semibold mb-2">Email Address</label>
          <input
            type="email"
            className="w-full px-4 py-2 border border-gray-600 rounded-md focus:outline-none focus:border-blue-500 bg-gray-700 text-gray-200"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="mb-6">
          <label className="block text-gray-400 text-sm font-semibold mb-2">Age</label>
          <input
            type="number"
            className="w-full px-4 py-2 border border-gray-600 rounded-md focus:outline-none focus:border-blue-500 bg-gray-700 text-gray-200"
            value={age}
            onChange={(e) => setAge(e.target.value)}
          />
        </div>
        <button
          type="submit"
          className="w-full bg-red-600 text-white p-3 rounded-md hover:bg-red-700"
        >
          Submit Ghost Story
        </button>
      </form>
    </div>
  );
}

export default Create;
