import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

function Read() {
  const [data, setData] = useState([]);
  const [error, setError] = useState('');

  async function getData() {
    const response = await fetch('http://localhost:5000');
    const result = await response.json();

    if (!response.ok) {
      console.log(result.error);
      setError(result.error);
    }
    if (response.ok) {
      setData(result);
      setError('');
    }
  }

  const handleDelete = async (id) => {
    const response = await fetch(`http://localhost:5000/${id}`, {
      method: 'DELETE',
    });
    const result = await response.json();

    if (!response.ok) {
      setError(result.error);
    }
    if (response.ok) {
      setError('Data Deleted Successfully');
      setTimeout(() => {
        setError('');
        getData();
      }, 1000);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <div className="container mx-auto my-8">
      {error && <div className="alert alert-danger">{error}</div>}
      <h2 className="text-4xl font-extrabold text-center mb-6 text-red-500">All Ghost Stories</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {data?.map((ele) => (
          <div key={ele._id} className="max-w-md h-full rounded overflow-hidden shadow-lg bg-gray-800 text-white">
            <div className="px-6 py-4">
              <h5 className="font-bold text-xl mb-2 text-blue-500 truncate">{ele.name}</h5>
              <p className="text-gray-300 text-base mb-2 truncate">{ele.story}</p>
              <div className="flex justify-between items-center">
                <div>
                  <h6 className="text-gray-400 text-sm mb-2 truncate">Email: {ele.email}</h6>
                  <p className="text-gray-300 text-base truncate">Advised age: {ele.age}</p>
                </div>
                <div>
                  <Link to={`/view/${ele._id}`} className="text-blue-500 hover:text-blue-700 ml-2">
                    View Story
                  </Link>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Read;
